import React, { Component } from 'react'
import UserService from '../services/UserService'
import PasienService from '../services/UserService'

class Dashboard extends Component {
    constructor(props) {
        super(props)

        this.state = {
                pasien: []
        }
        this.addPasien = this.addPasien.bind(this);
        this.editPasien = this.editPasien.bind(this);
        this.deletePasien = this.deletePasien.bind(this);
    }

    deletePasien(id){
        PasienService.deletePasien(id).then( res => {
            this.setState({pasien: 
                this.state.pasien.
                filter(pasien => pasien.id !== id)});
        });
    }
    viewPasien(id){
        this.props.history.push(`/view-pasien/${id}`);
    }
    editPasien(id){
        this.props.history.push(`/add-pasien/${id}`);
    }

    componentDidMount(){
        UserService.getUsers().then((res) => {
            if(res.data==null)
            {
                this.props.history.push('/add-user/_add');
            }
            this.setState({ users: res.data});
        });
    }

    addUser(){
        this.props.history.push('/add-user/_add');
    }

    render() {
        return (
            <div>
                 <h2 className="text-center">
                     Users List</h2>
                 <div className = "row">
                    <button className="btn btn-primary"
                     onClick={this.addUser}> Add User</button>
                 </div>
                 <br></br>
                 <div className = "row">
                        <table className 
                        = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> User First Name</th>
                                    <th> User Last Name</th>
                                    <th> User Email </th>
                                    <th> Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.users.map(
                                        user => 
                                        <tr key = {user.id}>
                                   <td> { user.firstName} </td>   
                                   <td> {user.lastName}</td>
                                   <td> {user.email}</td>
                                             <td>
                      <button onClick={ () => 
                          this.editUser(user.id)} 
                               className="btn btn-info">Update 
                                 </button>
                       <button style={{marginLeft: "10px"}}
                          onClick={ () => this.deleteUser(user.id)} 
                             className="btn btn-danger">Delete 
                                 </button>
                       <button style={{marginLeft: "10px"}} 
                           onClick={ () => this.viewUser(user.id)}
                              className="btn btn-info">View 
                                  </button>
                                    </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>
                 </div>
            </div>
        )
    }
}

export default Dashboard
